"use strict";

class Routing {
    constructor() {
        this.registry = new Map();
    }

    static _buildKey(url, method) {
        return method.toUpperCase() + ':' + url;
    }

    registerRouteHandler(url, method, handler) {
        this.registry.set(Routing._buildKey(url, method), handler);
    }

    getRouteHandler(url, method) {
        return this.registry.get(Routing._buildKey(url, method));
    }
}

module.exports = Routing;