"use strict";

var assert = require('assert');
var Stopwatch = require('./../stopwatch');

describe('stopwatch', function() {
    it('should record the passing of time accurately', function() {
        let sut = new Stopwatch();
        sut.start();

        sut.stop();

        let duration = sut.getDuration();
        assert(duration > 0);
        assert(duration < 500000);

        //Check that the stop watch has stopped
        assert.equal(sut.getDuration(), duration);
    });
});